// 获取本地ｉｐ
var os = require('os');
var ip = function() {
    // console.log('obtain an IP address');
}

ip.prototype.address = function() {
    var network = os.networkInterfaces();
    console.log(network);
    for(var i = 0; i < network.WLAN.length; i++) {
        var json = network.WLAN[i];
        if(json.family == 'IPv4') {
            console.log(json.address);
        }
    }
}
module.exports = ip;
